ruleSearch.bookList:
data.books[*]
@js:JSON.parse(result).map(o=> {
    var J = org.jsoup.Jsoup.parse(java.ajax(baseUrl.match(/(.+?\/\/.+?\/)/)[1] + 'source/' + o.sourceUuid));
    return {
        name: o.title,
        author: o.author,
        tags: o.keyName,
        num:o.showTotalCount+'字',
        latest: String(J.select('.m-book-recent a').text()).replace(/[\(（【].*[求更谢乐发合].*[】）\)]/,'') +String(J.select('.m-book-recent p').text()).replace(/\s*(\d+-\d+-\d+).+/, '（$1）').replace(/\s*(\d+)月(\d+)日.+/, '（$1-$2）'),
        intro: o.description.replace(/<\/?\w+>/g, '').trim(),
        cover: o.coverImage,
        catalogUrl: '/reader/book/info.json?catalog_only=true&source_uuid=' + o.sourceUuid
    }
});

.textlist!0
<js>
result.toArray().map(o=>{
    var detail = String(o.select('dt a').attr('href')).replace('//','https://');
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    return {
       name:  o.select('dt a').text(),
       author:String(o.select('.ls a').text()).replace('作者：',''),
       tags:  String(o.select('.bq span:eq(1)').text()).replace('类别：',''),
       num: String(o.select('.bq span:eq(2)').text()).replace('字数：',''),
       latest: String(J.select('dl.NewsChapter a').last().text()).replace(/[\(（].*[求更谢乐发订合补].*[）\)]/,''),
       intro: String(o.select('li:eq(2)').text()).replace('简介：','').trim(),
       cover: o.select('img').attr('src'),
       catalogUrl: 'https://www.17k.com'+J.select('.read a').attr('href')
    }
});
</js>

